import axios from 'axios';

const apiUrl = "http://localhost:5192"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/items`)    
    return result.data;
  },

  addTask: async(name)=>{
      try {
        const result = await axios.post(`${apiUrl}/items`, {
          "name": name,
          "isComplete": false 
        });

        console.log('addTask response:', result.data);

        return result.data;
      } catch (error) {
        console.error('addTask error:', error);
      }
},

  setCompleted: async(id, isComplete)=>{
    try {
      const result = await axios.put(`${apiUrl}/items/${id}`, {

        "isComplete": isComplete 
      });

      console.log('updateTask response:', result.data);

      return result.data;
    } catch (error) {
      console.error('updateTask error:', error);
    }
  },

  deleteTask:async()=>{
    console.log('deleteTask')
  }
};
