import axios from 'axios';

const apiUrl = "http://localhost:5192"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/items`)    
    return result.data;
  },

  addTask: async(name)=>{
      try {
        const result = await axios.post(`${apiUrl}/items`, {
          "name": name,
          "isComplete": false 
        });

        console.log('addTask response:', result.data);

        return result.data;
      } catch (error) {
        console.error('addTask error:', error);
      }
},

setCompleted: async (id,name, isComplete) => {
  try {
    console.log(isComplete);
    // Making a PUT or PATCH request to update the task's completion status]
    debugger;
    const result = await axios.put(`${apiUrl}/items/${id}`, {
      "name":name,
      "isComplete": isComplete
    });

    console.log('setCompleted response:', result.data);

    return result.data;
  } catch (error) {
    console.error('setCompleted error:', error);
    throw error; // Propagate the error to the calling function
  }
},



  deleteTask:async(id)=>{
    try {
      const result = await axios.delete(`${apiUrl}/items/${id}`);
      console.log('deleteTask response:', result.data);
      return result.data;
    } catch (error) {
      console.error('deleteTask error:', error);
    }
}
};
